import React, { Component } from 'react'
import { Container,Button } from 'react-bootstrap'
import Card from 'react-bootstrap/Card';

class App extends Component {
    state = {
        minutes: 3,
        seconds: 0,
    }

    componentDidMount() {
        this.myInterval = setInterval(() => {
            const { seconds, minutes } = this.state

            if (seconds > 0) {
                this.setState(({ seconds }) => ({
                    seconds: seconds - 1
                }))
            }
            if (seconds === 0) {
                if (minutes === 0) {
                    clearInterval(this.myInterval)
                } else {
                    this.setState(({ minutes }) => ({
                        minutes: minutes - 1,
                        seconds: 59
                    }))
                }
            }
        }, 1000)
    }

    componentWillUnmount() {
        clearInterval(this.myInterval)
    }

    render() {
        const { minutes, seconds } = this.state
        return (
            <Container style={{ margin: "auto" }}>
                <Card style={{ width: '18rem' ,margin:"2em 3em"}}>
                    <Card.Body>
                        <div>
                            {minutes === 0 && seconds === 0
                                ? <h1>Busted!</h1>
                                : <Card.Title>Time Remaining: {minutes}:{seconds < 10 ? `0${seconds}` : seconds}</Card.Title>
                            }
                            <Card.Subtitle className="mb-2 text-muted">React Timer</Card.Subtitle>
                        </div>


                        <Card.Text>
                            This Component Maked By Class Component 
                            Based On React Life Cycle
                            & componentDidMount &componentWillUnmount
                        </Card.Text>
                        <Button href="www.linkedin.com/in/yogeshwaran-s-37939021a"> Social Link</Button>
                        <Button href="https://www.youtube.com/channel/UCP23FSqQPw2bAFsNrgCYA5A"> Youtube Link</Button>

                    </Card.Body>
                </Card>
            </Container>
        )
    }
}
export default App;
